hudson.tasks.Shell.command:
# Calculate the date we are going to delete oldThan=$(date +"%Y%m%d" -d "-$OLD days") User=admin:$Artifact_pass
# this is a recursive function - check the last modified date, if old or equel to $oldThan than remove this folder from the Artifactory function RemoveFolder() {
local Folder=$1 # The date format is: YYYYMMDD (Y=the year, M=Month, D=Day - Example: 8 Jan 2018 should be 20180108 lastModified=$(curl -ks -X GET https://$ARTIFACTORY/api/storage/$Folder?lastModified | grep lastModified | cut -d'"' -f4 | cut -dT -f1 | sed s:-::g)
if [ "$lastModified" -le "$oldThan" ]; then echo Delete $Folder echo --------------------- curl -ks -u $User -X DELETE https://$ARTIFACTORY/$Folder else echo $Folder is not old - check its sub folders curl -ks -X GET https://$ARTIFACTORY/api/storage/$Folder | grep -B 1 -E 'folder.*true' | grep uri | cut -d'"' -f4 | while read uri do echo Execute this function again to check the following directory: ${Folder}$uri RemoveFolder ${Folder}$uri; done echo Check its files curl -ks -X GET https://$ARTIFACTORY/api/storage/$Folder | grep -B 1 -E 'folder.*false' | grep uri | cut -d'"' -f4 | while read uri do lastModified=$(curl -ks -X GET https://$ARTIFACTORY/api/storage/${Folder}$uri?lastModified | grep lastModified | cut -d'"' -f4 | cut -dT -f1 | sed s:-::g) if [ "$lastModified" -le "$oldThan" ]; then echo Delete ${Folder}$uri echo --------------------- curl -ks -u $User -X DELETE https://$ARTIFACTORY/${Folder}$uri done fi }
# Ececute for folders in Artifactory ARTIFACTORY=artifactory RemoveFolder ipp-review/nonLync RemoveFolder ipp-review/SFB
# Ececute for folders in test-Artifactory ARTIFACTORY=test-artifactory RemoveFolder sbc-review/com/ac/sbc/Firmware RemoveFolder sbc-review/com/ac/sbc/DRT RemoveFolder sbc-review/com/ac/sbc/user RemoveFolder ems-release/com/ac/ems/ovoc_server/7.4.1000/latest_iso RemoveFolder ems-release/com/ac/ems/ovoc_server/7.4.2000/latest_iso RemoveFolder ems-release/com/ac/ems/ovoc_server/7.4.3000/latest_iso RemoveFolder ems-release/com/ac/ems/ovoc_server/7.6/latest_iso
