hudson.tasks.Shell.command:
cd ~/.ssl vipw=$(openssl rsautl -decrypt -inkey private.pem -in pw.esxi)
cd ${WORKSPACE} vmHostIP="10.7.10.107" vm_username='armAdmin' vm_password='Arm!default1' vm_root_password='password'
# Create a new log file now=$(date +"%Y%m%d-%H%M%S") logfile="${WORKSPACE}/aws_armBuildVHD.log" [[ -f $logfile ]] && rm $logfile touch $logfile
#check if VM name ARM-Conf_$TAG is already exists vms=`ssh root@${vmHostIP} vim-cmd vmsvc/getallvms | grep "VHD_ARM-Conf_$TAG" | gawk '{print $1}'` if [ ! -z "$vms" ] then echo "$(date) : VHD_ARM-Conf_$TAG already exist, I am exiting now" | tee -a $logfile exit 1 fi
#check if VM name ARM-Router_$TAG is already exists vms=`ssh root@${vmHostIP} vim-cmd vmsvc/getallvms | grep "VHD_ARM-Router_$TAG" | gawk '{print $1}'` if [ ! -z "$vms" ] then echo "$(date) : VHD_ARM-Router_$TAG already exist, I am exiting now" | tee -a $logfile exit 1 fi
#create a new VM from ARM-Conf_image.ova (ova file is located on jenkins slave echo "$(date) : creating new Conf VM from image" | tee -a $logfile OVA=/home/centos/jenkinsslave_jenkins/arm_ova/Configurator/Configurator_Centos8.ovf ovftool --disableVerification --noSSLVerify --name="VHD_ARM-Conf_$TAG" --datastore=DataStore01 --diskMode=thin --acceptAllEulas --powerOn ${WORKSPACE}/ovaFiles/ARM-Conf_${TAG}.ova vi://root:$vipw@${vmHostIP} if [ $? -ne 0 ]; then echo "$(date) : Deploy new Configuator VM has failed" | tee -a $logfile exit 1 fi #create a new VM from ARM-Router_$TAG.ova (ova file is located on jenkins slave echo "$(date) : creating new Router VM from image" ovftool --disableVerification --noSSLVerify --name="VHD_ARM-Router_$TAG" --datastore=DataStore01 --diskMode=thin --acceptAllEulas --powerOn ${WORKSPACE}/ovaFiles/ARM-Router_${TAG}.ova vi://root:$vipw@${vmHostIP} if [ $? -ne 0 ]; then echo "$(date) : Deploy new Router VM has failed" | tee -a $logfile exit 1 fi
# find new arm configurator vm id conf_vmid=`ssh root@${vmHostIP} vim-cmd vmsvc/getallvms | grep "VHD_ARM-Conf_$TAG" | gawk '{print $1}'` echo "$(date) : vm id is $conf_vmid" | tee -a $logfile
# find new arm configurator vm id router_vmid=`ssh root@${vmHostIP} vim-cmd vmsvc/getallvms | grep "VHD_ARM-Router_$TAG" | gawk '{print $1}'` echo "$(date) : vm id is $router_vmid" | tee -a $logfile
# Wait for power on echo "$(date) : sleep 5 min." | tee -a $logfile sleep 300
# find new arm configurator vm IPv4 address conf_vmIPaddress=`ssh root@${vmHostIP} vim-cmd vmsvc/get.guest $conf_vmid | grep -E "ipAddress.*\[$" -m 1 -A 5 | grep -E -o "([0-9]{1,3}[\.]){3}[0-9]{1,3}"` ipcalc -cs $conf_vmIPaddress || ( echo "$(date) : Invalid IP for $conf_vmid" | exit 1) echo "$(date) : Configurator vm IP is $conf_vmIPaddress" | tee -a $logfile
# find new arm router vm IPv4 address router_vmIPaddress=`ssh root@${vmHostIP} vim-cmd vmsvc/get.guest $router_vmid | grep -E "ipAddress.*\[$" -m 1 -A 5 | grep -E -o "([0-9]{1,3}[\.]){3}[0-9]{1,3}"` ipcalc -cs $router_vmIPaddress || ( echo "$(date) : Invalid IP for $router_vmid" | exit 1) echo "$(date) : Router vm IP is $router_vmIPaddress" | tee -a $logfile
function modifyNetwork () { HOST=$1 sshpass -p ${vm_password} ssh -oStrictHostKeyChecking=no ${vm_username}@${HOST} << EOF sudo -i -u root bash << EOS set -xe echo 'DEVICE=eth0' > "/etc/sysconfig/network-scripts/ifcfg-eth0" echo 'ONBOOT=yes' >> "/etc/sysconfig/network-scripts/ifcfg-eth0" echo 'BOOTPROTO=dhcp' >> "/etc/sysconfig/network-scripts/ifcfg-eth0" echo 'TYPE=Ethernet' >> "/etc/sysconfig/network-scripts/ifcfg-eth0" echo 'USERCTL=no' >> "/etc/sysconfig/network-scripts/ifcfg-eth0" echo 'PEERDNS=yes' >> "/etc/sysconfig/network-scripts/ifcfg-eth0" echo 'IPV6INIT=no' >> "/etc/sysconfig/network-scripts/ifcfg-eth0" echo 'NM_CONTROLLED=no' >> "/etc/sysconfig/network-scripts/ifcfg-eth0" EOS EOF }
function modifyUdevRules () { HOST=$1 sshpass -p ${vm_password} ssh -oStrictHostKeyChecking=no ${vm_username}@${HOST} << EOF sudo -i -u root bash << EOS set -xe sudo ln -s /dev/null /etc/udev/rules.d/75-persistent-net-generator.rules EOS EOF }
function updateYum () { HOST=$1 sshpass -p ${vm_password} ssh -oStrictHostKeyChecking=no ${vm_username}@${HOST} << EOF sudo -i -u root bash << EOS set -e sudo dnf clean all && rm -r /var/cache/dnf && sudo dnf upgrade -y && sudo dnf update -y #sudo shutdown --reboot +0 && exit EOS EOF }
function modifyKernelBoot () { HOST=$1 sshpass -p ${vm_password} ssh -oStrictHostKeyChecking=no ${vm_username}@${HOST} << EOF sudo -i -u root bash << EOS set -xe sed -i.bak '/GRUB_CMDLINE_LINUX/ s/$/ GRUB_CMDLINE_LINUX=\"rootdelay=300 console=tty0 console=ttyS0 earlyprintk=ttyS0 net.ifnames=0\"/' /etc/default/grub sed -i 's/rhgb quiet/edd=off/g' /etc/default/grub sed -i 's/crashkernel=auto//g' /etc/default/grub sudo grub2-mkconfig -o /boot/grub2/grub.cfg echo add_drivers+=\" hv_vmbus hv_netvsc hv_storvsc \" >> /etc/dracut.conf sudo dracut -f -v EOS EOF }
function installWaagent () { HOST=$1 sshpass -p ${vm_password} ssh -oStrictHostKeyChecking=no ${vm_username}@${HOST} << EOF sudo -i -u root bash << EOS set -xe #sed -i '/ResourceDisk.Format=/d' /etc/waagent.conf echo 'ResourceDisk.Format=y' >> /etc/waagent.conf #sed -i '/ResourceDisk.Filesystem=/d' /etc/waagent.conf echo 'ResourceDisk.Filesystem=ext4' >> /etc/waagent.conf #sed -i '/ResourceDisk.MountPoint=/d' /etc/waagent.conf echo 'ResourceDisk.MountPoint=/mnt/resource' >> /etc/waagent.conf #sed -i '/ResourceDisk.EnableSwap=/d' /etc/waagent.conf echo 'ResourceDisk.EnableSwap=y' >> /etc/waagent.conf #sed -i '/ResourceDisk.SwapSizeMB=/d' /etc/waagent.conf echo 'ResourceDisk.SwapSizeMB=4096' >> /etc/waagent.conf #sed -i '/Provisioning.DeleteRootPassword=/d' /etc/waagent.conf echo 'Provisioning.DeleteRootPassword=n' >> /etc/waagent.conf sudo dnf install -y python3-pyasn1 WALinuxAgent sudo systemctl enable waagent
EOS EOF }
function disableSwap () { HOST=$1 sshpass -p ${vm_password} ssh -oStrictHostKeyChecking=no ${vm_username}@${HOST} <<EOF sudo -i -u root bash <<EOS set -e swapoff -a sed -i '/swap/ s/^/#/' /etc/fstab #sudo shutdown --reboot +0 && exit EOS EOF }
function clearHistory () { HOST=$1 sshpass -p ${vm_password} ssh -oStrictHostKeyChecking=no ${vm_username}@${HOST} <<EOF sudo -i -u root bash <<EOS set -xe history -w history -c EOS EOF }
function prepareForAzure () { HOST=$1 sshpass -p ${vm_password} ssh -oStrictHostKeyChecking=no ${vm_username}@${HOST} <<EOF sudo -i -u root bash <<EOS set -xe sudo waagent -deprovision+user -force export HISTSIZE=0 EOS EOF }
function blockRootAccess () { HOST=$1 sshpass -p ${vm_password} ssh -oStrictHostKeyChecking=no ${vm_username}@${HOST} <<EOF sudo -i -u root bash <<EOS set -xe sed -i 's/#PermitRootLogin yes/PermitRootLogin no/g' /etc/ssh/sshd_config EOS EOF }
function reduceVMSize () { HOST=$1 sshpass -p ${vm_password} ssh -oStrictHostKeyChecking=no ${vm_username}@${HOST} <<EOF sudo -i -u root bash <<EOS set -xe dd if=/dev/zero of=zeroFile.tmp || echo finished rm -rf zeroFile.tmp EOS EOF }
function prepareMachine () { HOST=$1 modifyNetwork $HOST modifyUdevRules $HOST updateYum $HOST echo "sleep 120 seconds after reboot" sleep 120 modifyKernelBoot $HOST installWaagent $HOST blockRootAccess $HOST disableSwap $HOST echo "sleep 120 seconds after reboot" sleep 120 clearHistory $HOST prepareForAzure $HOST
}
function prepareMachineForAzure () { HOST=$1 sshpass -p ${vm_password} ssh -oStrictHostKeyChecking=no ${vm_username}@${HOST} << EOF sudo -i -u root bash << EOS set -xe #Modify Network Rules echo 'DEVICE=eth0' > "/etc/sysconfig/network-scripts/ifcfg-eth0" echo 'ONBOOT=yes' >> "/etc/sysconfig/network-scripts/ifcfg-eth0" echo 'BOOTPROTO=dhcp' >> "/etc/sysconfig/network-scripts/ifcfg-eth0" echo 'TYPE=Ethernet' >> "/etc/sysconfig/network-scripts/ifcfg-eth0" echo 'USERCTL=no' >> "/etc/sysconfig/network-scripts/ifcfg-eth0" echo 'PEERDNS=yes' >> "/etc/sysconfig/network-scripts/ifcfg-eth0" echo 'IPV6INIT=no' >> "/etc/sysconfig/network-scripts/ifcfg-eth0" echo 'NM_CONTROLLED=no' >> "/etc/sysconfig/network-scripts/ifcfg-eth0" #Modify Udev Rules sudo ln -s /dev/null /etc/udev/rules.d/75-persistent-net-generator.rules #Update Yum sudo dnf clean all && rm -r /var/cache/dnf && sudo dnf upgrade -y && sudo dnf update -y #Modify Kernel Boot sed -i.bak '/GRUB_CMDLINE_LINUX/ s/$/ GRUB_CMDLINE_LINUX=\"rootdelay=300 console=ttyS0 earlyprintk=ttyS0 net.ifnames=0\"/' /etc/default/grub sed -i 's/rhgb quiet//g' /etc/default/grub sed -i 's/crashkernel=auto//g' /etc/default/grub sudo grub2-mkconfig -o /boot/grub2/grub.cfg echo add_drivers+=\" hv_vmbus hv_netvsc hv_storvsc \" >> /etc/dracut.conf sudo dracut -f -v #Install Waagent #sed -i '/ResourceDisk.Format=/d' /etc/waagent.conf echo 'ResourceDisk.Format=y' >> /etc/waagent.conf #sed -i '/ResourceDisk.Filesystem=/d' /etc/waagent.conf echo 'ResourceDisk.Filesystem=ext4' >> /etc/waagent.conf #sed -i '/ResourceDisk.MountPoint=/d' /etc/waagent.conf echo 'ResourceDisk.MountPoint=/mnt/resource' >> /etc/waagent.conf #sed -i '/ResourceDisk.EnableSwap=/d' /etc/waagent.conf echo 'ResourceDisk.EnableSwap=y' >> /etc/waagent.conf #sed -i '/ResourceDisk.SwapSizeMB=/d' /etc/waagent.conf echo 'ResourceDisk.SwapSizeMB=4096' >> /etc/waagent.conf #sed -i '/Provisioning.DeleteRootPassword=/d' /etc/waagent.conf echo 'Provisioning.DeleteRootPassword=n' >> /etc/waagent.conf sudo dnf install -y python3-pyasn1 WALinuxAgent sudo systemctl enable waagent #block root Access sed -i 's/#PermitRootLogin yes/PermitRootLogin no/g' /etc/ssh/sshd_config #disable swap swapoff -a sed -i '/swap/ s/^/#/' /etc/fstab #clean logs systemctl stop tomcat rm -rf /opt/tomcat/logs/* || true find /var/log -type f -delete || true semanage fcontext -a -t var_log_t -s system_u '/opt/tomcat/logs(/.*)?' || true restorecon -Frvv /opt/tomcat/logs || true #clear history history -w history -c #deprovision machine with waagent sudo waagent -deprovision+user -force export HISTSIZE=0 EOS EOF }
prepareMachineForAzure $conf_vmIPaddress prepareMachineForAzure $router_vmIPaddress
#wait for VM to UP sleep 100
#graceful shutdouwn BOTH VM echo "$(date) : shutdown new Configurator VM" | tee -a $logfile ssh -oStrictHostKeyChecking=no root@${vmHostIP} vim-cmd vmsvc/power.shutdown ${conf_vmid} sleep 30
echo "$(date) : shutdown new Router VM" | tee -a $logfile ssh -oStrictHostKeyChecking=no root@${vmHostIP} vim-cmd vmsvc/power.shutdown ${router_vmid} sleep 30
#export both VMS to OVA mkdir $WORKSPACE/newOVAs echo "$(date) : export new Configurator VM to an OVA file" | tee -a $logfile ovftool -o --noSSLVerify --powerOffSource vi://root:$vipw@${vmHostIP}/VHD_ARM-Conf_$TAG $WORKSPACE/newOVAs/VHD_ARM-Conf_$TAG.ova | tee -a $logfile ovfResult=$(tail -1 $logfile | grep "Completed") if [[ "${ovfResult}" == "Completed with errors" ]] then exit 1 fi cd $WORKSPACE/newOVAs mv VHD_ARM-Conf_$TAG.ova ARM-Conf_$TAG.ova
echo "$(date) : export new Router VM to an OVA file" | tee -a $logfile ovftool -o --noSSLVerify --powerOffSource vi://root:$vipw@${vmHostIP}/VHD_ARM-Router_$TAG $WORKSPACE/newOVAs/VHD_ARM-Router_$TAG.ova | tee -a $logfile ovfResult=$(tail -1 $logfile | grep "Completed") if [[ "${ovfResult}" == "Completed with errors" ]] then exit 1 fi cd $WORKSPACE/newOVAs mv VHD_ARM-Router_$TAG.ova ARM-Router_$TAG.ova
#terminate both VM MACHINES echo "$(date) : delete new Configurator VM" | tee -a $logfile ssh -oStrictHostKeyChecking=no root@${vmHostIP} vim-cmd vmsvc/destroy ${conf_vmid} sleep 30
echo "$(date) : delete new Router VM" | tee -a $logfile ssh -oStrictHostKeyChecking=no root@${vmHostIP} vim-cmd vmsvc/destroy ${router_vmid} sleep 30
