secureGroovyScript.script:
import hudson.*
import jenkins.model.*


ProjectName=currentBuild.properties.environment.GERRIT_PROJECT
map=null
if (ProjectName.equals("DSP/Gen5/repo_manifests"))
{
return [REFSPEC: "+refs/heads/${GERRIT_BRANCH}:refs/remotes/origin/${GERRIT_BRANCH}"]
}
return map

hudson.tasks.Shell.command:
#!c:\cygwin64\bin\bash --login -i set -xe cd ${WORKSPACE}
echo "repo init -u ssh://jenkins@gerrit:29418/DSP/Gen5/repo_manifests -b ${GERRIT_BRANCH}" repo init -u ssh://jenkins@gerrit:29418/DSP/Gen5/repo_manifests -b ${GERRIT_BRANCH} echo "repo sync" repo sync -j$(nproc) --force-sync
# cd to releant path cd Gen5
echo "git fetch ssh://jenkins@gerrit:29418/DSP/Gen5 $GERRIT_REFSPEC" git fetch ssh://jenkins@gerrit:29418/DSP/Gen5 $GERRIT_REFSPEC && git checkout FETCH_HEAD #copy all modules to relevant path in Gen5 Modules directory echo "sh copyModules.sh" sh copyModules.sh echo "cp /cygdrive/c/TI/NMAKE/NMAKE.EXE ${WORKSPACE}/Gen5/${CHIP_NAME}/Device/NMAKE.EXE" cp /cygdrive/c/TI/NMAKE/NMAKE.EXE ${WORKSPACE}/Gen5/${CHIP_NAME}/Device/NMAKE.EXE
echo "******************* change ${CHIP_NAME} makefile version number****************************" OLDTAG=$(git describe --first-parent --tags | sed 's/\(.*\)-.*/\1/') VER=${OLDTAG} VERSION=$(echo ${OLDTAG} | cut -d'.' -f1) SUBVERSION=$(echo ${OLDTAG} | cut -d'.' -f2) sed -i "s/%major.minor%/${VER}/g" ${CHIP_NAME}/Device/makefile sed -i "s/%major%/${VERSION}/g" ${CHIP_NAME}/Device/makefile sed -i "s/%minor%/${SUBVERSION}/g" ${CHIP_NAME}/Device/makefile echo "*******************************************************************************************"
ls -al ${WORKSPACE}/Gen5/Modules/Ec ls -al ${WORKSPACE}/Gen5/Modules cd ${WORKSPACE} TARGET=`echo $GERRIT_REFSPEC | sed 's:refs/changes/../::; s:/:-:g'` echo TARGET=${TARGET} >> generated_dsp.properties
